example.code:
using (var et = new EntityTransaction<Administrator>())
{
    var admin = Administrator.FindByName("admin");
    admin.Logins++;
    admin.Update();

    et.Commit();
}

using (var et = new EntityTransaction(DAL.Create("Common")))
{
    var admin = Administrator.FindByName("admin");
    admin.Logins++;
    admin.Update();

    et.Commit();
}
